package com.OddNumbers.jnit;

/* Print Odd Numbers from 1 to 10 in reverse order */
public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 10; i > 1; i--) {
			if (i % 2 == 1) {
				System.out.println("Odd Numbers " +"\t"+i); // ODD Number Logic
			}
		}

	}

}
